import { useState } from 'react'
import styles from './InputForm.module.css'

const InputForm = (props) => {
  const [currentSavings, setCurrentSavings] = useState('')
  const [yearlyContriburion, setYearlyContriburion] = useState('')
  const [expectedReturn, setExpectedReturn] = useState('')
  const [duration, setDuration] = useState('')

  const formValue = (e) => {
    e.preventDefault()
  }

  const savingsHandler = (e) => {
    setCurrentSavings(e.target.value)
  }

  const contributionHandler = (e) => {
    setYearlyContriburion(e.target.value)
  }

  const returnHandler = (e) => {}

  const durationHandler = (e) => {}

  return (
    <form className={styles.form} onSubmit={formValue}>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='current-savings'>Current Savings ($)</label>
          <input
            type='number'
            id='current-savings'
            value={currentSavings}
            onChange={savingsHandler}
          />
        </p>
        <p>
          <label htmlFor='yearly-contribution'>Yearly Savings ($)</label>
          <input
            value={yearlyContriburion}
            type='number'
            id='yearly-contribution'
            onChange={contributionHandler}
          />
        </p>
      </div>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='expected-return'>
            Expected Interest (%, per year)
          </label>
          <input
            value={expectedReturn}
            type='number'
            id='expected-return'
            onChange={returnHandler}
          />
        </p>
        <p>
          <label htmlFor='duration'>Investment Duration (years)</label>
          <input
            value={expectedReturn}
            type='number'
            id='duration'
            onChange={durationHandler}
          />
        </p>
      </div>
      <p className={styles.actions}>
        <button type='reset' className={styles.buttonAlt}>
          Reset
        </button>
        <button type='submit' className={styles.button}>
          Calculate
        </button>
      </p>
    </form>
  )
}

export default InputForm
