import styles from './Input.module.css'

const Input = (props) => {
  const handleValue = (e) => {
		if(e.target.value.trim())
    props.setValue(e.target.value)
  }
  return (
    <p>
      <label htmlFor={props.id}>{props.label}</label>
      <input
        type={props.type || 'number'}
        id={props.id}
        value={props.value}
        onChange={handleValue}
      />
    </p>
  )
}
export default Input
