import { useState } from 'react'
import logo from './assets/investment-calculator-logo.png'
import Header from './components/Header/Header'
import InputForm from './components/InputForm/InputForm'
import InvestmentData from './components/InvesmentData/InvesmentData'

const formatter = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD',
  minimumFractionDigits: 2,
  maximumFractionDigits: 2,
})

const App = () => {
  const [yearlyData, SetYearlyData] = useState([])

  const calculateHandler = (userInput) => {
    const INITAL_ARR = []

    let currentSavings = +userInput.currentSavings
    const yearlyContribution = +userInput.yearlyContriburion
    const expectedReturn = +userInput.expectedReturn / 100
    const duration = +userInput.duration

    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn
      const year = i + 1
      currentSavings += yearlyInterest + yearlyContribution
      INITAL_ARR.push({
        year: year,
        yearlyInterest: formatter.format(yearlyInterest),
        totalInterest: formatter.format(currentSavings * year),
        savingsEndOfYear: formatter.format(currentSavings),
        yearlyContribution: formatter.format(yearlyContribution),
      })
    }
    SetYearlyData(INITAL_ARR)
  }

  return (
    <div>
      <Header logo={logo} alt={'logo'} />
      <InputForm calculateHandler={calculateHandler} />
      {yearlyData.length > 0 && <InvestmentData yearlyData={yearlyData} />}
    </div>
  )
}

export default App
