import styles from './Input.module.css'

const Input = (props) => {
	const handleValue = (e) =>{
		props.setValue(e.target.value)
	}
  return (
    <p>
      <label htmlFor='current-savings'>{props.label}</label>
      <input
        type='number'
        id={props.id}
        value={props.value}
        onChange={handleValue}
      />
    </p>
  )
}
export default Input
