import { useState } from 'react'
import styles from './InputForm.module.css'
import Input from './Input/Input'

const InputForm = (props) => {
  const [currentSavings, setCurrentSavings] = useState('')
  const [yearlyContriburion, setYearlyContriburion] = useState('')
  const [expectedReturn, setExpectedReturn] = useState('')
  const [duration, setDuration] = useState('')
  const [isFormValid, setIsFormValid] = useState(false)
  let errorText = ''
  const formValue = (e) => {
    e.preventDefault()

    if (!isFormValid) {
      errorText = <p className={styles.errorText}>Please fill out the fields</p>
    }
  }

  return (
    <form className={styles.form} onSubmit={formValue}>
      <div className={styles['input-group']}>
        <Input
          label='Current Savings ($)'
          id='current-savings'
          value={currentSavings}
          setValue={setCurrentSavings}
          isFormValid={setIsFormValid}
        />
        <Input
          label='Yearly Savings  ($)'
          id='yearly-contribution'
          value={yearlyContriburion}
          setValue={setYearlyContriburion}
          isFormValid={setIsFormValid}
        />
      </div>
      <div className={styles['input-group']}>
        <Input
          label='Expected Interest (%, per year)'
          id='expected-return'
          value={expectedReturn}
          setValue={setExpectedReturn}
          isFormValid={setIsFormValid}
        />
        <Input
          label='Investment Duration (years)'
          id='duration'
          value={duration}
          setValue={setDuration}
          isFormValid={setIsFormValid}
        />
      </div>
      <p className={styles.actions}>
        <button type='reset' className={styles.buttonAlt}>
          Reset
        </button>
        <button type='submit' className={styles.button}>
          Calculate
        </button>
      </p>
      {!isFormValid && errorText}
    </form>
  )
}

export default InputForm
